/**
 * This “API” provides a global table to allow you to refer to sides / directions by name, as opposed to their numbers.
 * @see https://ocdoc.cil.li/api:sides
 * @noSelf
 * @noResolution
 */
declare module "sides" {
    const bottom = 0;
    const top = 1;
    const back = 2;
    const front = 3;
    const right = 4;
    const left = 5;
}
